import os

import numpy as np

######################################################################
######################### General parameter ##########################
# True if the Gazebo simulator is used; False for the iCub-simulator
GAZEBO_SIM = False
# normally no need to change; only if multiple user work in the same YARP-network
CLIENT_PREFIX = "client"
# set to robot name: normally "iCubSim" for simulation and "icub" for real robot
ROBOT_PREFIX = "icubSim"

MODE = "iCub_ANN" ## "yarp"; "icub_pylib"; "iCub_ANN"
PATH_TO_INTERFACE_BUILD = "/home/toto/Schreibtisch/Interface_ANNarchy_iCub/build"
INTERFACE_INI_PATH = os.path.abspath("./data/") + "/"

######################################################################
###################### Transformation matrices: ######################

###### iCub_Sim coordinate system <-> gazebo coordinate system #######
Transfermat_gazebo2iCubSim = np.array([[0., 1., 0., 0.0000],
                                       [0., 0., 1., 0.0000],
                                       [1., 0., 0., 0.0000],
                                       [0., 0., 0., 1.0000]])
Transfermat_iCubSim2gazebo = np.linalg.inv(Transfermat_gazebo2iCubSim)

######## robot coordinate system <-> gazebo coordinate system ########
Transfermat_robot2gazebo = np.array([[-1., 0., 0., -0.0260],
                                     [0., -1., 0., 0.0000],
                                     [0., 0., 1., 0.5976],
                                     [0., 0., 0., 1.0000]])
Transfermat_gazebo2robot = np.linalg.inv(Transfermat_robot2gazebo)

####### robot coordinate system <-> iCub_Sim coordinate system #######
Transfermat_robot2iCubSim = np.array([[0., -1., 0., 0.0000],
                                      [0., 0., 1., 0.5976],
                                      [-1., 0., 0., -0.0260],
                                      [0., 0., 0., 1.0000]])
Transfermat_iCubSim2robot = np.linalg.inv(Transfermat_robot2iCubSim)

######################################################################
############### example hand position and orientation ################
# in world coordinate system
pos_hand_world_coord = np.array([-0.15, 0.8, 0.2, 1.0])
# in robot coordinate system
orientation_robot_hand = np.array([0.0022, -0.993, -0.1178, 2.0423])


#### set transformation matrices according to selected simulator #####
if GAZEBO_SIM:
    Transfermat_robot2world = Transfermat_robot2gazebo
    Transfermat_world2robot = Transfermat_gazebo2robot
    pos_hand_world_coord = np.dot(
        Transfermat_iCubSim2gazebo, pos_hand_world_coord.reshape((4, 1)))
else:
    Transfermat_robot2world = Transfermat_robot2iCubSim
    Transfermat_world2robot = Transfermat_iCubSim2robot


